import React, { useState } from "react";
import "./question.css";
import question_icon from "../../assets/svg/question_icon.svg";
import answer_icon from "../../assets/svg/answer_icon.svg";

const Question = ({ question, answer, question_answer_icon }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleAnswer = () => {
    if (isOpen) {
      setIsOpen(false);
      question_answer_icon = question_icon;
    } else {
      setIsOpen(true);
      question_answer_icon = answer_icon;
    }
  };

  return (
    <div className="question-container">
      <div className="question" onClick={toggleAnswer}>
        {question}
        <img src={question_answer_icon} alt="question_icon" />
      </div>
      {isOpen && <div className="answer">{answer}</div>}
    </div>
  );
};

export default Question;
