import React from "react";
import "./how_it_works.css";
import how_it_works_img from "../../assets/svg/how_it_works_img.svg";
import Advantage from "../advantages/Advantage";

const requireIcon = require.context(
  "../../assets/svg/advantages_icons",
  false,
  /\.svg$/
);

// Create an array of icons with dynamic imports
const customIcons = requireIcon.keys().map((fileName) => {
  const icon = requireIcon(fileName);
  const id = fileName.replace("./", "").replace(".svg", "");
  return { id, icon: icon.default };
});

const HIW = () => {
  return (
    <div className="how_it_works section__padding">
      <div className="how_it_works-content">
        <div className="article_block">
          <p>How it works</p>
        </div>
        <h1>Building the best space for collaboration.</h1>
        <div className="how_it_works_advantage-list">
          {customIcons.map((item) => (
            <Advantage key={item.key} advantage_list_element_icon={item.icon} />
          ))}
        </div>
      </div>
      <div className="how_it_works-image">
        <img src={how_it_works_img} alt="how_it_works_img" />
      </div>
    </div>
  );
};

export default HIW;
